    }

    public int getCooldown() {
        return cooldown;
    }

    public void decrementCooldown() {
        --cooldown;
    }

    @Override
    public boolean canUse() {
        if (cooldown  0时即AI在冷却时返回false
            return false;
        }
    }

    @Override
    public void start() {
        cooldown = reducedTickDelay(200);
        super.start();
    }
}

NearestAttackableWitchTargetGoal：
public class NearestAttackableWitchTargetGoal extends NearestAttackableTargetGoal {
    private boolean canAttack = true;

    public NearestAttackableWitchTargetGoal(Raider raider, Class targetType, int randomInterval, boolean mustSee, boolean mustReach, @Nullable Predicate targetSelector) {
        super(raider, targetType, randomInterval, mustSee, mustReach, targetSelector);
    }

    public void setCanAttack(boolean canAttack) {
        this.canAttack = canAttack;
    }

    @Override
    public boolean canUse() {
        return canAttack && super.canUse();
    }
}

